// src/components/ProductComponent.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProductComponent = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ prodName: '', price: 0 });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({
      ...newProduct,
      [name]: value
    });
  };

  const addProduct = async () => {
    try {
      const newProdId = Math.max(...products.map(p => p.prodId), 0) + 1;
      const productToAdd = { ...newProduct, prodId: newProdId };

      await axios.post('/products', productToAdd);
      fetchProducts();
      setNewProduct({ prodName: '', price: 0 });
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const deleteProduct = async (prodId) => {
    try {
      await axios.delete(`/products/id==${prodId}`);
      fetchProducts();
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  const getRandomThumbnail = async () => {
    const response = await axios.get('https://api.pexels.com/v1/curated?per_page=1', {
      headers: { 'Content-Type': 'application/json' }
    });
    const photos = response.data.photos;
    const randomPhoto = photos[Math.floor(Math.random() * photos.length)];
    return randomPhoto.src.original;
  };

  return (
    <div>
      <h2>Products</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
        {products.map(async (product) => {
          const thumbnail = await getRandomThumbnail();
          return (
            <div key={product.prodId} style={{ width: '150px', textAlign: 'center' }}>
              <img src={thumbnail} alt={product.prodName} style={{ width: '100%', height: '100px', objectFit: 'cover' }} />
              <p>{product.prodName}</p>
              <p>${product.price}</p>
              <button onClick={() => deleteProduct(product.prodId)}>Delete</button>
            </div>
          );
        })}
      </div>

      <h2>Add New Product</h2>
      <input
        type="text"
        name="prodName"
        value={newProduct.prodName}
        onChange={handleInputChange}
        placeholder="Product Name"
      />
      <input
        type="number"
        name="price"
        value={newProduct.price}
        onChange={handleInputChange}
        placeholder="Price"
      />
      <button onClick={addProduct}>Add Product</button>
    </div>
  );
};

export default ProductComponent;
