// src/components/ProductComponent.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProductComponent = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ prodName: '', price: 0 });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({
      ...newProduct,
      [name]: value
    });
  };

  const addProduct = async () => {
    try {
      // Generate a new dynamic ID (you can use a more sophisticated method if needed)
      const newProdId = Math.max(...products.map(p => p.prodId), 0) + 1;
      const productToAdd = { ...newProduct, prodId: newProdId };

      await axios.post('/products', productToAdd);
      fetchProducts();
      setNewProduct({ prodName: '', price: 0 });
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const deleteProduct = async (prodId) => {
    try {
      await axios.delete(`/products/id==${prodId}`);
      fetchProducts();
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  const getRandomThumbnail = () => {
    const thumbnails = [
      'https://via.placeholder.com/50',
      'https://via.placeholder.com/50/FF0000',
      'https://via.placeholder.com/50/00FF00',
      'https://via.placeholder.com/50/0000FF'
    ];
    return thumbnails[Math.floor(Math.random() * thumbnails.length)];
  };

  return (
    <div>
      <h2>Products</h2>
      <table>
        <thead>
          <tr>
            <th>Thumbnail</th>
            <th>Product</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.prodId}>
              <td>
                <img src={getRandomThumbnail()} alt={product.prodName} style={{ width: '50px', height: '50px' }} />
              </td>
              <td>{product.prodName}</td>
              <td>${product.price}</td>
              <td>
                <button onClick={() => deleteProduct(product.prodId)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Add New Product</h2>
      <input
        type="text"
        name="prodName"
        value={newProduct.prodName}
        onChange={handleInputChange}
        placeholder="Product Name"
      />
      <input
        type="number"
        name="price"
        value={newProduct.price}
        onChange={handleInputChange}
        placeholder="Price"
      />
      <button onClick={addProduct}>Add Product</button>
    </div>
  );
};

export default ProductComponent;
