// src/components/ProductComponent.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProductComponent = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ prodName: '', price: 0 });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({
      ...newProduct,
      [name]: value
    });
  };

  const addProduct = async () => {
    try {
      await axios.post('/products', newProduct);
      fetchProducts();
      setNewProduct({ prodName: '', price: 0 });
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const deleteProduct = async (prodId) => {
    try {
      await axios.delete(`/products/${prodId}`);
      fetchProducts();
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div>
      <h2>Products</h2>
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.prodId}>
              <td>{product.prodName}</td>
              <td>${product.price}</td>
              <td>
                <button onClick={() => deleteProduct(product.prodId)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Add New Product</h2>
      <input
        type="text"
        name="prodName"
        value={newProduct.prodName}
        onChange={handleInputChange}
        placeholder="Product Name"
      />
      <input
        type="number"
        name="price"
        value={newProduct.price}
        onChange={handleInputChange}
        placeholder="Price"
      />
      <button onClick={addProduct}>Add Product</button>
    </div>
  );
};

export default ProductComponent;
